import React, { Component, useState, useEffect } from 'react';
import { apiHost } from 'config';
import axios from 'axios';
import Quizzes from './quizzes';
import { isCompositeComponent } from 'react-dom/test-utils';

class QuizzesContainer extends Component {

    state = {
        quizzes: [],
        pageNumber: 0,
        hasMore: false,
        loading: true,
        theTopic: ""
    };

    fetchQuizzesPaginated = async () => {
        this.setState({loading: true});
        const requests = [], { pageNumber } = this.state, size = 20;

        for (let i = 0; i <= this.state.pageNumber; i++) 
            requests.push(axios.get(`${apiHost}/api/quizzes`, {
                params: {
                    page: pageNumber,
                    size: size
                }
            }))

        const responses = await Promise.all(requests);
        let data = [];
        responses.forEach(response => data = [...data, ...response.data])

        const lastResponseLength = responses[responses.length-1].data.length;
        this.setState({hasMore: lastResponseLength === size});

        this.setState({loading: false, quizzes: data})
    }

    fetchQuizzesByTopic(topic) {
        axios.get(`${apiHost}/api/quizzes`, {
            params: { topic }
        }).then(response => {
            this.setState({
                quizzes: response.data,
                pageNumber: 0
            })
        })
    }

    componentDidMount() {
        this.fetchQuizzesPaginated();
    }

    reset() {
        this.setState({
            quizzes: [],
            pageNumber: 0,
            hasMore: false,
            loading: true
        }, this.fetchQuizzesPaginated)
    }

    filterByTopic = topic => {
        if(topic === "") {
            this.reset();
        } else {
            console.log("setting topic")
            this.setState({theTopic: topic})
            this.fetchQuizzesByTopic(topic)
        }
    }

    render() {

        const { quizzes, hasMore, loading } = this.state;

        return(
            <div className="portal">
                <Quizzes 
                    quizzes={quizzes} 
                    filterByTopic={this.filterByTopic}
                    hasMore={hasMore} 
                    loading={loading}

                    incrementPageNumber={() => this.setState(prevState => {
                        return {
                            ...prevState,   
                            pageNumber: prevState.pageNumber + 1
                        }
                    }, this.fetchQuizzesPaginated)}
                 />
            </div>
        )
    }


}

export default QuizzesContainer;
