import React, { useRef, useCallback } from 'react';
import QuizPreview from 'components/portal/quizPreview';
import { webHost } from 'config';
import Filter from './filter';
import Loading from '../loading';

const Quizzes = ({ quizzes, filterByTopic, hasMore, loading, incrementPageNumber, theTopic }) => {

    console.log(loading)

    const observer = useRef();

    const lastQuizElementRef = useCallback(node => {

      if (loading) return;
      if (observer.current) observer.current.disconnect();

      observer.current = new IntersectionObserver(entries => {

        if (entries[0].isIntersecting && hasMore) {
            incrementPageNumber();
        } 
      })

      if (node) observer.current.observe(node);
    }, [loading, hasMore]);

    return(
        <div className="container">  

            {!quizzes ? <Loading /> :

                <>
                    <h3 className="title has-text-weight-light">Here are quizzes people have made...</h3>

                    <h5 className="has-text-grey">See quizzes by topic</h5>
                    <div style={{maxWidth: "550px"}}>
                        <Filter filterByTopic={filterByTopic} />
                        <br />
                    </div>

                    {quizzes && quizzes.map((quiz, idx) => {
                        const url = `${webHost}/quizzes/${quiz.id}`, quizJSX = <QuizPreview key={idx} url={url} quiz={quiz} showCreator={true} />;

                        const css = { margin: "25px 0" };

                        if (quizzes.length === idx + 1) 
                            return(
                                <div style={css} key={idx} ref={lastQuizElementRef}>
                                    {quizJSX}
                                </div>
                            ) 

                        return <div style={css} key={idx}>{quizJSX}</div>
                    })}

                    <button onClick={() => window.location = "/create"} style={{marginTop: "10px"}} className="button is-info">Create a Quiz</button>
                </>
            }

        </div>
    )
}


export default Quizzes;
